import { useState } from "react";

function ChapterTranscript() {
  const [activeSection, setActiveSection] = useState("chapter"); // Default to Chapter

  // Styles for the tab-based header
  const styles = {
    header: {
      display: "flex",
      justifyContent: "center",
      backgroundColor: "#222",
      padding: "0.75rem 0",
      width: "100%",
      boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.2)",
    },
    tab: {
      padding: "0.5rem 1.5rem",
      backgroundColor: "transparent",
      color: "#fff",
      fontSize: "1rem",
      border: "none",
      cursor: "pointer",
      transition: "color 0.3s ease",
    },
    activeTab: {
      padding: "0.5rem 1.5rem",
      backgroundColor: "#00ff9d",
      color: "#000",
      fontSize: "1rem",
      fontWeight: "bold",
      border: "none",
      cursor: "pointer",
      borderRadius: "6px",
      transition: "background-color 0.3s ease",
    },
    contentBox: {
      marginTop: "1rem",
      backgroundColor: "transparent",
      padding: "1rem",
      borderRadius: "6px",
      color: "#fff",
      height: "150%",
      width: "100%",
      maxWidth: "800px",
      textAlign: "center",
      boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
      opacity: 1,
      transform: "translateY(0px)",
      transition: "opacity 0.3s ease, transform 0.3s ease",
    },
  };

  return (
    <div>
      {/* Header Tabs */}
      <div style={styles.header}>
        <button
          style={activeSection === "chapter" ? styles.activeTab : styles.tab}
          onClick={() => setActiveSection("chapter")}
        >
          Chapter
        </button>
        <button
          style={activeSection === "transcript" ? styles.activeTab : styles.tab}
          onClick={() => setActiveSection("transcript")}
        >
          Transcript
        </button>
      </div>

      {/* Content Section */}
      <div style={styles.contentBox}>
        {activeSection === "chapter" && (
          <div>
            <h3 style={{ marginBottom: "0.5rem", color: "#00ff9d" }}>
              Chapter 1: Introduction
            </h3>
            <p>In this chapter, we introduce the main concepts of the video.</p>
          </div>
        )}
        {activeSection === "transcript" && (
          <div>
            <h3 style={{ marginBottom: "0.5rem", color: "#00ff9d" }}>
              Transcript
            </h3>
            <p>This is the full transcript of the video...</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default ChapterTranscript;
