import { useState } from "react";

function ChapterTranscript() {
  const [activeSection, setActiveSection] = useState(null); // Track active section (chapter or transcript)

  const showChapter = () => setActiveSection("chapter");
  const showTranscript = () => setActiveSection("transcript");

  return (
    <div
      style={{
        marginTop: "1rem",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <div
        style={{
          marginTop: "1rem",
          display: "flex",
          justifyContent: "center",
          gap: "1rem",
        }}
      >
        <button
          onClick={showChapter}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: "#333",
            color: "#fff",
            fontSize: "1rem",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          Show Chapter
        </button>
        <button
          onClick={showTranscript}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: "#333",
            color: "#fff",
            fontSize: "1rem",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          Show Transcript
        </button>
      </div>
      {/* Conditionally render chapter or transcript based on activeSection */}
      <div
        style={{
          marginTop: "1rem",
          backgroundColor: "#2c2c2c",
          padding: "1rem",
          borderRadius: "6px",
          color: "#fff",
          flex: 0, // Keeps this section fixed in height
          maxHeight: "200px", // Can be adjusted based on your preference
          overflowY: "auto",
        }}
      >
        {activeSection === "chapter" && (
          <div
            style={{
              marginTop: "1rem",
            }}
          >
            <h3>Chapter 1: Introduction</h3>
            <p>In this chapter, we introduce the main concepts of the video.</p>
          </div>
        )}
        {activeSection === "transcript" && (
          <div
            style={{
              marginTop: "1rem",
            }}
          >
            <h3>Transcript</h3>
            <p>This is the full transcript of the video...</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default ChapterTranscript;
