import { useRef, useState } from "react";

function VideoPlayer() {
  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [activeSection, setActiveSection] = useState(null); // Track active section (chapter or transcript)
  const [videoSrc, setVideoSrc] = useState(""); // Track video URL
  const [isUploading, setIsUploading] = useState(false); // State to track whether the user is uploading

  const togglePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const showChapter = () => setActiveSection("chapter");
  const showTranscript = () => setActiveSection("transcript");

  const handleVideoUpload = () => {
    setIsUploading(true); // Show the input field to upload the video URL
  };

  const handleVideoLinkSubmit = (e) => {
    e.preventDefault();
    const link = e.target.elements.videoLink.value.trim();
    if (link) {
      setVideoSrc(link); // Set the video URL
      setIsUploading(false); // Hide the input field
    }
  };

  return (
    <div style={styles.videoContainer}>
      {!videoSrc && !isUploading ? (
        <div style={styles.uploadContainer}>
          <button onClick={handleVideoUpload} style={styles.uploadButton}>
            Upload Video
          </button>
        </div>
      ) : isUploading ? (
        <form onSubmit={handleVideoLinkSubmit} style={styles.uploadForm}>
          <input
            type="text"
            name="videoLink"
            placeholder="Enter video URL"
            style={styles.uploadInput}
            autoFocus
          />
          <button type="submit" style={styles.uploadSubmitButton}>
            Submit
          </button>
        </form>
      ) : (
        <>
          <video ref={videoRef} style={styles.video} src={videoSrc} controls={false} />
          <div style={styles.controls}>
            <button onClick={togglePlayPause} style={styles.button}>
              {isPlaying ? "Pause" : "Play"}
            </button>
          </div>
          <div style={styles.additionalControls}>
            <button onClick={showChapter} style={styles.toggleButton}>
              Show Chapter
            </button>
            <button onClick={showTranscript} style={styles.toggleButton}>
              Show Transcript
            </button>
          </div>
          {/* Conditionally render chapter or transcript based on activeSection */}
          {activeSection === "chapter" && (
            <div style={styles.chapter}>
              <h3>Chapter 1: Introduction</h3>
              <p>In this chapter, we introduce the main concepts of the video.</p>
            </div>
          )}
          {activeSection === "transcript" && (
            <div style={styles.transcript}>
              <h3>Transcript</h3>
              <p>This is the full transcript of the video...</p>
            </div>
          )}
        </>
      )}
    </div>
  );
}

const styles = {
  videoContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#1e1e1e",
    padding: "1.5rem",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.3)",
    maxWidth: "800px",
    margin: "auto",
  },
  uploadContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  uploadButton: {
    padding: "0.6rem 1.2rem",
    backgroundColor: "#00ff9d",
    color: "#000",
    fontSize: "1rem",
    fontWeight: "bold",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
  },
  uploadForm: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  uploadInput: {
    padding: "0.6rem 1.2rem",
    marginBottom: "0.5rem",
    borderRadius: "4px",
    border: "1px solid #333",
    width: "80%",
    color: "#fff",
    backgroundColor: "#222",
  },
  uploadSubmitButton: {
    padding: "0.6rem 1.2rem",
    backgroundColor: "#00ff9d",
    color: "#000",
    fontSize: "1rem",
    fontWeight: "bold",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
  },
  video: {
    width: "100%",
    borderRadius: "8px",
    outline: "none",
  },
  controls: {
    marginTop: "0.8rem",
  },
  button: {
    padding: "0.6rem 1.2rem",
    backgroundColor: "#00ff9d",
    color: "#000",
    fontSize: "1rem",
    fontWeight: "bold",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
  },
  additionalControls: {
    marginTop: "1rem",
    display: "flex",
    justifyContent: "center",
    gap: "1rem",
  },
  toggleButton: {
    padding: "0.5rem 1rem",
    backgroundColor: "#333",
    color: "#fff",
    fontSize: "1rem",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
  },
  chapter: {
    marginTop: "1rem",
    backgroundColor: "#2c2c2c",
    padding: "1rem",
    borderRadius: "6px",
    color: "#fff",
  },
  transcript: {
    marginTop: "1rem",
    backgroundColor: "#2c2c2c",
    padding: "1rem",
    borderRadius: "6px",
    color: "#fff",
  },
};

export default VideoPlayer;
