import { useState } from "react";
import Chat from "../components/Chat";
import Flashcards from "../components/Flashcards";
import Summary from "../components/Summary";
import VideoPlayer from "../components/VideoPlayer"; // Import the VideoPlayer component
import ChapterTranscript from "../components/ChapterTranscript"; // Import ChapterTranscript component

function Home() {
  const [activeTab, setActiveTab] = useState("chat");

  return (
    <div style={styles.appContainer}>
      <header style={styles.header}>
        <div style={styles.logo}>YouLearn</div>
      </header>

      <div style={styles.mainContent}>
        <div style={styles.leftContainer}>
          <div style={styles.videoSection}>
            <VideoPlayer videoSrc="https://www.w3schools.com/html/mov_bbb.mp4" /> {/* Video player on the left */}
          </div>

          <div style={styles.chapterTranscriptSection}>
            <ChapterTranscript /> {/* Chapter and Transcript section at the bottom-left */}
          </div>
        </div>

        <div style={styles.rightContainer}>
          <div style={styles.tabs}>
            <button
              style={activeTab === "chat" ? styles.activeTab : styles.tab}
              onClick={() => setActiveTab("chat")}
            >
              Chat
            </button>
            <button
              style={activeTab === "flashcards" ? styles.activeTab : styles.tab}
              onClick={() => setActiveTab("flashcards")}
            >
              Flashcards
            </button>
            <button
              style={activeTab === "summary" ? styles.activeTab : styles.tab}
              onClick={() => setActiveTab("summary")}
            >
              Summary
            </button>
          </div>

          <div style={styles.content}>
            {activeTab === "chat" && <Chat />}
            {activeTab === "flashcards" && <Flashcards />}
            {activeTab === "summary" && <Summary />}
          </div>
        </div>
      </div>
    </div>
  );
}

const styles = {
  appContainer: {
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    backgroundColor: "#1a1a1a",
    color: "#fff",
  },
  header: {
    display: "flex",
    alignItems: "center",
    padding: "0.5rem 1rem",
    backgroundColor: "#141414",
    borderBottom: "1px solid #333",
  },
  logo: {
    fontSize: "1.25rem",
    fontWeight: "bold",
    marginRight: "auto",
  },
  button: {
    marginLeft: "0.5rem",
    padding: "0.25rem 0.75rem",
    borderRadius: "4px",
    border: "none",
    background: "transparent",
    color: "#fff",
    cursor: "pointer",
  },
  upgrade: {
    backgroundColor: "#00ff9d",
    color: "#1a1a1a",
  },
  mainContent: {
    display: "flex",
    flex: 1,
  },
  leftContainer: {
    width: "45%", // Set the width of the left container
    display: "flex",
    flexDirection: "column",
  },
  rightContainer: {
    width: "45%", // Reduce the width of the right container from 55% to 45%
    display: "flex",
    flexDirection: "column",
    padding: "1rem", // Add some padding for internal spacing
  },
  videoSection: {
    height: "50vh", // Set the height of the video section (adjust as needed)
    padding: "1rem",
    backgroundColor: "#333",
    display: "flex",
    flexDirection: "column",
    width: "100%", // Ensure the video player takes full width of the section
  },
  chapterTranscriptSection: {
    width: "100%", // Ensure it takes full width
    padding: "1rem",
    backgroundColor: "#333",
    display: "flex",
    flexDirection: "column",
    borderTop: "1px solid #444",
    marginTop: "1rem",
  },
  tabs: {
    display: "flex",
    padding: "0.5rem",
    backgroundColor: "#141414",
    borderBottom: "1px solid #333",

  },
  tab: {
    background: "transparent",
    border: "none",
    color: "#a0a0a0",
    padding: "0.5rem 1rem",
    cursor: "pointer",
  },
  activeTab: {
    color: "#fff",
    borderBottom: "2px solid #00ff9d",
  },
  content: {
    flex: 1,
    width: "100%", // Ensure content takes full width of the container
    padding: "1rem",
    backgroundColor: "#222", // Slightly dark background for content area
  },
};

export default Home;
