import { useState } from "react";
import YouTube from "react-youtube";
import ChapterTranscript from "./ChapterTranscript"; // Import the ChapterTranscript component

function VideoPlayer() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [videoId, setVideoId] = useState(""); // Track YouTube video ID
  const [isUploading, setIsUploading] = useState(false); // State to track whether the user is uploading

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const handleVideoUpload = () => {
    setIsUploading(true); // Show the input field to upload the video URL
  };

  const handleVideoLinkSubmit = (e) => {
    e.preventDefault();
    const link = e.target.elements.videoLink.value.trim();
    if (link) {
      const videoId = extractYouTubeId(link); // Extract YouTube video ID
      setVideoId(videoId); // Set the video ID
      setIsUploading(false); // Hide the input field
    }
  };

  // Helper function to extract YouTube video ID from URL
  const extractYouTubeId = (url) => {
    const regex = /(?:youtube\.com\/(?:[^\/\n\s]+\/\S+\/|(?:v|e(?:mbed)?)\/|(?:v=|\S*?[?&]v=))|youtu\.be\/)([^"&?\/\s]{11})/;
    const match = url.match(regex);
    return match ? match[1] : "";
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        height: "100vh", // Ensure it takes full viewport height
        backgroundColor: "#1e1e1e",
        padding: "1rem",
        boxSizing: "border-box",
        borderRadius: "10px",
        boxShadow: "0 4px 10px rgba(0, 0, 0, 0.3)",
        maxWidth: "1000px",
        margin: "auto",
      }}
    >
      {!videoId && !isUploading ? (
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <button
            onClick={handleVideoUpload}
            style={{
              padding: "0.6rem 1.2rem",
              backgroundColor: "#00ff9d",
              color: "#000",
              fontSize: "1rem",
              fontWeight: "bold",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
            }}
          >
            Upload Video
          </button>
        </div>
      ) : isUploading ? (
        <form
          onSubmit={handleVideoLinkSubmit}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <input
            type="text"
            name="videoLink"
            placeholder="Enter YouTube video URL"
            style={{
              padding: "0.6rem 1.2rem",
              marginBottom: "0.5rem",
              borderRadius: "4px",
              border: "1px solid #333",
              width: "80%",
              color: "#fff",
              backgroundColor: "#222",
            }}
            autoFocus
          />
          <button
            type="submit"
            style={{
              padding: "0.6rem 1.2rem",
              backgroundColor: "#00ff9d",
              color: "#000",
              fontSize: "1rem",
              fontWeight: "bold",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
            }}
          >
            Submit
          </button>
        </form>
      ) : (
        <>
          <div
            style={{
              flex: 1, // Ensures the video player takes up all available space
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              width: "100%",
              height: "100%",
            }}
          >
            <YouTube
              videoId={videoId}
              opts={{
                width: "100%",
                height: "100%",
                playerVars: { autoplay: 1 },
              }}
              onPlay={() => setIsPlaying(true)}
              onPause={() => setIsPlaying(false)}
            />
          </div>
          <div
            style={{
              marginTop: "1rem",
              textAlign: "center",
            }}
          >
            <button
              onClick={togglePlayPause}
              style={{
                padding: "0.6rem 1.2rem",
                backgroundColor: "#00ff9d",
                color: "#000",
                fontSize: "1rem",
                fontWeight: "bold",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
              }}
            >
              {isPlaying ? "Pause" : "Play"}
            </button>
          </div>
          <ChapterTranscript />
        </>
      )}
    </div>
  );
}

export default VideoPlayer;
