import { useState } from "react";

function ChapterTranscript() {
  const [activeTab, setActiveTab] = useState("chapter"); // Default to Chapter

  return (
    <div style={styles.container}>
      {/* Tab Header */}
      <div style={styles.tabs}>
        {["chapter", "transcript"].map((tab) => (
          <button
            key={tab}
            style={{
              ...styles.tab,
              ...(activeTab === tab ? styles.activeTab : {}),
            }}
            onMouseEnter={(e) => (e.target.style.color = "#fff")}
            onMouseLeave={(e) =>
              (e.target.style.color = activeTab === tab ? "#fff" : "#a0a0a0")
            }
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {/* Content Section */}
      <div style={styles.content}>
        {activeTab === "chapter" && (
          <div>
            <h3 style={styles.title}>Chapter 1: Introduction</h3>
            <p>In this chapter, we introduce the main concepts of the video.</p>
          </div>
        )}
        {activeTab === "transcript" && (
          <div>
            <h3 style={styles.title}>Transcript</h3>
            <p>This is the full transcript of the video...</p>
          </div>
        )}
      </div>
    </div>
  );
}

// Styles (same theme as Home.js)
const styles = {
  container: {
    backgroundColor: "transparent",
    borderRadius: "8px",
    padding: "1rem",
    borderTop: "1px solid #333",
    flex: 1,
    overflowY: "auto",
  },
  tabs: {
    display: "flex",
    justifyContent: "space-around",
    padding: "0.5rem",
  },
  tab: {
    flex: 1,
    textAlign: "center",
    padding: "0.75rem",
    cursor: "pointer",
    backgroundColor: "transparent",
    border: "none",
    color: "#a0a0a0",
    fontSize: "1rem",
    transition: "color 0.3s ease, background-color 0.3s ease",
  },
  activeTab: {
    color: "#fff",
    borderBottom: "3px solid #00ff9d",
    fontWeight: "bold",
  },
  content: {
    padding: "1rem",
    backgroundColor: "transparent",
    borderRadius: "8px",
    overflowY: "auto",
    color: "#e0e0e0",
  },
  title: {
    marginBottom: "0.5rem",
    color: "#00ff9d",
  },
};

export default ChapterTranscript;
