import React, { useState } from "react";

const DUMMY_DATA = [
  { id: 1, question: "What is AI?", answer: "AI is artificial intelligence." },
  { id: 2, question: "What is Machine Learning?", answer: "Machine Learning is a subset of AI." },
];

function Flashcards() {
  const [currentCard, setCurrentCard] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);

  const handlePrev = () => {
    setShowAnswer(false); // Reset the answer view
    setCurrentCard((currentCard - 1 + DUMMY_DATA.length) % DUMMY_DATA.length);
  };

  const handleNext = () => {
    setShowAnswer(false); // Reset the answer view
    setCurrentCard((currentCard + 1) % DUMMY_DATA.length);
  };

  const Flashcard = ({ content, onClick, showAnswer }) => {
    return (
      <div style={styles.flashcard} onClick={onClick}>
        {showAnswer ? content.answer : content.question}
      </div>
    );
  };

  return (
    <div style={styles.flashcardContainer}>
      <Flashcard
        content={DUMMY_DATA[currentCard]}
        onClick={() => setShowAnswer(!showAnswer)}
        showAnswer={showAnswer}
      />
      <div style={styles.controls}>
        <button style={styles.button} onClick={handlePrev}>
          ← Previous
        </button>
        <button style={styles.button} onClick={handleNext}>
          Next →
        </button>
      </div>
    </div>
  );
}

const styles = {
  flashcardContainer: {
    textAlign: "center",
    padding: "2rem",
    backgroundColor: "#f4f4f9",
    minHeight: "100vh",
  },
  flashcard: {
    padding: "2rem",
    margin: "1rem auto",
    width: "90%",
    maxWidth: "400px",
    backgroundColor: "#ffffff",
    color: "#333",
    borderRadius: "12px",
    boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
    fontSize: "1.2rem",
    textAlign: "center",
    cursor: "pointer",
    transition: "transform 0.2s",
  },
  controls: {
    marginTop: "1rem",
    display: "flex",
    justifyContent: "center",
    gap: "1rem",
  },
  button: {
    padding: "0.8rem 1.5rem",
    backgroundColor: "#6200ea",
    color: "#ffffff",
    border: "none",
    borderRadius: "8px",
    fontSize: "1rem",
    cursor: "pointer",
    transition: "background-color 0.2s",
  },
};

export default Flashcards;
