import { useState } from "react";
import Chat from "../components/Chat";
import Flashcards from "../components/Flashcards";
import Summary from "../components/Summary";
import VideoPlayer from "../components/VideoPlayer"; // Import the VideoPlayer component
import ChapterTranscript from "../components/ChapterTranscript"; // Import ChapterTranscript component

function Home() {
  const [activeTab, setActiveTab] = useState("chat");

  return (
    <div style={styles.appContainer}>
      <header style={styles.header}>
        <div style={styles.logo}>YouLearn</div>
      </header>

      <div style={styles.mainContent}>
        <div style={styles.videoSection}>
          <VideoPlayer /> {/* Video player at the top */}
        </div>

        <div style={styles.chapterTranscriptSection}>
          <ChapterTranscript /> {/* Chapter and Transcript section below the video */}
        </div>

        <div style={styles.tabs}>
          <button
            style={activeTab === "chat" ? styles.activeTab : styles.tab}
            onClick={() => setActiveTab("chat")}
          >
            Chat
          </button>
          <button
            style={activeTab === "flashcards" ? styles.activeTab : styles.tab}
            onClick={() => setActiveTab("flashcards")}
          >
            Flashcards
          </button>
          <button
            style={activeTab === "summary" ? styles.activeTab : styles.tab}
            onClick={() => setActiveTab("summary")}
          >
            Summary
          </button>
        </div>

        <div style={styles.content}>
          {activeTab === "chat" && <Chat />}
          {activeTab === "flashcards" && <Flashcards />}
          {activeTab === "summary" && <Summary />}
        </div>
      </div>
    </div>
  );
}

const styles = {
  appContainer: {
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    backgroundColor: "#0f0f0f", // Darker background for the app
    color: "#e0e0e0", // Light gray text color for better contrast
    fontFamily: "Arial, sans-serif", // Clean, modern font
  },
  header: {
    display: "flex",
    alignItems: "center",
    padding: "1rem",
    backgroundColor: "#1e1e1e", // Slightly lighter header for separation
    borderBottom: "1px solid #333",
    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.2)",
  },
  logo: {
    fontSize: "1.5rem",
    fontWeight: "bold",
    color: "#00ff9d", // Bright accent color for the logo
  },
  button: {
    marginLeft: "0.5rem",
    padding: "0.5rem 1rem",
    borderRadius: "4px",
    border: "none",
    backgroundColor: "#333", // Subtle button background
    color: "#fff",
    cursor: "pointer",
    transition: "background-color 0.3s",
  },
  upgrade: {
    backgroundColor: "#00ff9d",
    color: "#0f0f0f",
    fontWeight: "bold",
  },
  mainContent: {
    display: "flex",
    flexDirection: "column", // Stack content vertically
    gap: "1rem", // Space between sections
    padding: "1rem",
  },
  videoSection: {
    height: "60vh", // Increased height of the video player to 60% of the viewport height
    backgroundColor: "#202020",
    padding: "1rem",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    marginBottom: "1rem",
  },
  chapterTranscriptSection: {
    padding: "1rem",
    backgroundColor: "#222",
    borderTop: "1px solid #333",
    marginBottom: "1rem",
    flex: 1,
    overflowY: "auto",
  },
  tabs: {
    display: "flex",
    backgroundColor: "#1e1e1e",
    padding: "0.5rem",
    borderBottom: "1px solid #333",
  },
  tab: {
    flex: 1,
    textAlign: "center",
    padding: "0.75rem",
    cursor: "pointer",
    backgroundColor: "transparent",
    border: "none",
    color: "#a0a0a0",
    fontSize: "1rem",
    transition: "color 0.3s, border-bottom 0.3s",
  },
  activeTab: {
    color: "#fff",
    borderBottom: "3px solid #00ff9d", // Highlight for active tab
    fontWeight: "bold",
  },
  content: {
    padding: "1rem",
    backgroundColor: "#1e1e1e",
    borderRadius: "8px",
    overflowY: "auto",
  },
  "@media (max-width: 768px)": {
    mainContent: {
      flexDirection: "column",
    },
  },
};

export default Home;
