import { useState } from "react";

function Chat() {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message.trim()) {
      // Add user message to the chat
      setMessages([...messages, { text: message, sender: "user" }]);
      setMessage("");

      // Simulate a dummy bot response after 1 second
      setTimeout(() => {
        const botResponse = {
          text: "This is a dummy response to your query!",
          sender: "bot",
        };
        setMessages((prevMessages) => [...prevMessages, botResponse]);
      }, 1000); // Simulate response after 1 second
    }
  };

  return (
    <div style={styles.chatContainer}>
      <div style={styles.messages}>
        <div style={styles.message}>Welcome to the chat!</div>
        {messages.map((msg, index) => (
          <div key={index} style={msg.sender === "user" ? styles.userMessage : styles.message}>{msg.text}</div>
        ))}
      </div>
      <form style={styles.inputContainer} onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Ask anything..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          style={styles.input}
        />
      </form>
    </div>
  );
}

const styles = {
  chatContainer: { display: "flex", flexDirection: "column", height: "100%" },
  messages: { flex: 1, overflowY: "auto", padding: "1rem" },
  message: { margin: "0.5rem 0", padding: "0.5rem 1rem", backgroundColor: "#0f0f0f", borderRadius: "4px" },
  userMessage: { backgroundColor: "#0f0f0f", color: "#000", marginLeft: "auto" },
  inputContainer: { padding: "1rem" },
  input: { width: "100%", padding: "0.5rem", backgroundColor: "transparent ", border: "1px solid #333", borderRadius: "4px", color: "#fff" },
};

export default Chat;
