import { useState } from "react";

function ChapterTranscript() {
  const [activeSection, setActiveSection] = useState(null); // Track active section (chapter or transcript)

  return (
    <div
      style={{
        marginTop: "1rem",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      {/* Header Bar */}
      <div
        style={{
          width: "100%",
          backgroundColor: "#222", // Dark header
          padding: "1rem 0",
          textAlign: "center",
          color: "#00ff9d",
          fontSize: "1.5rem",
          fontWeight: "bold",
          boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.2)",
        }}
      >
        Chapter & Transcript
      </div>

      {/* Buttons */}
      <div
        style={{
          marginTop: "1rem",
          display: "flex",
          justifyContent: "center",
          gap: "1rem",
        }}
      >
        <button
          onClick={() => setActiveSection("chapter")}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: activeSection === "chapter" ? "#00ff9d" : "#333",
            color: "#fff",
            fontSize: "1rem",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            transition: "background-color 0.3s ease",
          }}
        >
          Show Chapter
        </button>

        <button
          onClick={() => setActiveSection("transcript")}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: activeSection === "transcript" ? "#00ff9d" : "#333",
            color: "#fff",
            fontSize: "1rem",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            transition: "background-color 0.3s ease",
          }}
        >
          Show Transcript
        </button>
      </div>

      {/* Content Box */}
      {activeSection && (
        <div
          style={{
            marginTop: "1rem",
            backgroundColor: "#2c2c2c",
            padding: "1rem",
            borderRadius: "6px",
            color: "#fff",
            height: "150%",
            width: "100%", // Responsive width
            maxWidth: "800px", // Prevents excessive width
            textAlign: "center",
            boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)", // Adds depth
            opacity: activeSection ? 1 : 0,
            transform: activeSection ? "translateY(0px)" : "translateY(10px)",
            transition: "opacity 0.3s ease, transform 0.3s ease",
          }}
        >
          {activeSection === "chapter" && (
            <div>
              <h3 style={{ marginBottom: "0.5rem", color: "#00ff9d" }}>
                Chapter 1: Introduction
              </h3>
              <p>In this chapter, we introduce the main concepts of the video.</p>
            </div>
          )}
          {activeSection === "transcript" && (
            <div>
              <h3 style={{ marginBottom: "0.5rem", color: "#00ff9d" }}>
                Transcript
              </h3>
              <p>This is the full transcript of the video...</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default ChapterTranscript;
