import { useState } from "react";

const DUMMY_DATA = [
  { id: 1, question: "What is AI?", answer: "AI is artificial intelligence." },
  { id: 2, question: "What is Machine Learning?", answer: "Machine Learning is a subset of AI." },
];

function Flashcards() {
  const [currentCard, setCurrentCard] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);

  return (
    <div style={styles.flashcardContainer}>
      <div style={styles.flashcard} onClick={() => setShowAnswer(!showAnswer)}>
        {showAnswer ? DUMMY_DATA[currentCard].answer : DUMMY_DATA[currentCard].question}
      </div>
      <div style={styles.controls}>
        <button onClick={() => setCurrentCard((currentCard - 1 + DUMMY_DATA.length) % DUMMY_DATA.length)}>←</button>
        <button onClick={() => setCurrentCard((currentCard + 1) % DUMMY_DATA.length)}>→</button>
      </div>
    </div>
  );
}

const styles = {
  flashcardContainer: { textAlign: "center" },
  flashcard: { padding: "1.5rem", backgroundColor: "#222", borderRadius: "8px", cursor: "pointer" },
  controls: { marginTop: "1rem" },
};

export default Flashcards;
