import { useRef, useState } from "react";

function VideoPlayer({ videoSrc }) {
  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [activeSection, setActiveSection] = useState(null); // Track active section (chapter or transcript)

  const togglePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const showChapter = () => setActiveSection("chapter");
  const showTranscript = () => setActiveSection("transcript");

  return (
    <div style={styles.videoContainer}>
      <video ref={videoRef} style={styles.video} src={videoSrc} controls={false} />
      <div style={styles.controls}>
        <button onClick={togglePlayPause} style={styles.button}>
          {isPlaying ? "Pause" : "Play"}
        </button>
      </div>

      <div style={styles.additionalControls}>
        <button onClick={showChapter} style={styles.toggleButton}>
          Show Chapter
        </button>
        <button onClick={showTranscript} style={styles.toggleButton}>
          Show Transcript
        </button>
      </div>

      {/* Conditionally render chapter or transcript based on activeSection */}
      {activeSection === "chapter" && (
        <div style={styles.chapter}>
          <h3>Chapter 1: Introduction</h3>
          <p>In this chapter, we introduce the main concepts of the video.</p>
        </div>
      )}

      {activeSection === "transcript" && (
        <div style={styles.transcript}>
          <h3>Transcript</h3>
          <p>This is the full transcript of the video...</p>
        </div>
      )}
    </div>
  );
}

const styles = {
  videoContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#1e1e1e",
    padding: "1.5rem",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.3)",
    maxWidth: "800px",
    margin: "auto",
  },
  video: {
    width: "100%",
    borderRadius: "8px",
    outline: "none",
  },
  controls: {
    marginTop: "0.8rem",
  },
  button: {
    padding: "0.6rem 1.2rem",
    backgroundColor: "#00ff9d",
    color: "#000",
    fontSize: "1rem",
    fontWeight: "bold",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
  },
  additionalControls: {
    marginTop: "1rem",
    display: "flex",
    justifyContent: "center",
    gap: "1rem",
  },
  toggleButton: {
    padding: "0.5rem 1rem",
    backgroundColor: "#333",
    color: "#fff",
    fontSize: "1rem",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
  },
  chapter: {
    marginTop: "1rem",
    backgroundColor: "#2c2c2c",
    padding: "1rem",
    borderRadius: "6px",
    color: "#fff",
  },
  transcript: {
    marginTop: "1rem",
    backgroundColor: "#2c2c2c",
    padding: "1rem",
    borderRadius: "6px",
    color: "#fff",
  },
};

export default VideoPlayer;
