import { useState } from "react";

function ChapterTranscript() {
  const [activeSection, setActiveSection] = useState(null); // Track active section (chapter or transcript)

  // Dummy JSON data
  const data = {
    chapters: [
      { time: "00:30", title: "Introduction to AI" },
      { time: "02:15", title: "What is Machine Learning?" },
      { time: "05:40", title: "Deep Learning Overview" },
      { time: "08:20", title: "Neural Networks Explained" },
      { time: "12:45", title: "AI in Real Life" },
      { time: "15:30", title: "Future of AI" },
      { time: "20:00", title: "Conclusion" },
    ],
    transcript: [
      "Welcome to this session on Artificial Intelligence. Today, we will cover the basics of AI and its applications.",
      "Artificial Intelligence has been revolutionizing various industries, from healthcare to finance. Machine Learning, a subset of AI, enables systems to learn from data and make decisions.",
      "Deep Learning, which uses neural networks, has significantly improved tasks like image recognition and natural language processing.",
      "AI is also being used in autonomous vehicles, chatbots, and recommendation systems. Companies leverage AI to enhance customer experiences and optimize processes.",
      "Ethical AI and bias in AI models are growing concerns, and researchers are working to make AI more transparent and fair.",
    ],
  };

  return (
    <div
      style={{
        marginTop: "1rem",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      {/* Buttons */}
      <div
        style={{
          marginTop: "1rem",
          display: "flex",
          justifyContent: "center",
          gap: "1rem",
        }}
      >
        <button
          onClick={() => setActiveSection("chapter")}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: activeSection === "chapter" ? "#00ff9d" : "#333",
            color: "#fff",
            fontSize: "1rem",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            transition: "background-color 0.3s ease",
          }}
        >
          Show Chapters
        </button>

        <button
          onClick={() => setActiveSection("transcript")}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: activeSection === "transcript" ? "#00ff9d" : "#333",
            color: "#fff",
            fontSize: "1rem",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            transition: "background-color 0.3s ease",
          }}
        >
          Show Transcript
        </button>
      </div>

      {/* Content Box */}
      {activeSection && (
        <div
          style={{
            marginTop: "1rem",
            backgroundColor: "#2c2c2c",
            padding: "1rem",
            borderRadius: "6px",
            color: "#fff",
            width: "100%", // Responsive width
            maxWidth: "800px", // Prevents excessive width
            textAlign: "center",
            boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)", // Adds depth

          }}
        >
          {activeSection === "chapter" && (
            <div>
              <h3 style={{ marginBottom: "0.5rem", color: "#00ff9d" }}>Chapters</h3>
              <ul style={{ textAlign: "left", padding: "0 1rem", listStyleType: "none" }}>
                {data.chapters.map((chapter, index) => (
                  <li key={index} style={{ padding: "5px 0", borderBottom: "1px solid #444" }}>
                    ⏱ {chapter.time} - {chapter.title}
                  </li>
                ))}
              </ul>
            </div>
          )}
          {activeSection === "transcript" && (
            <div>
              <h3 style={{ marginBottom: "0.5rem", color: "#00ff9d" }}>Full Transcript</h3>
              {data.transcript.map((paragraph, index) => (
                <p key={index} style={{ marginBottom: "10px", textAlign: "justify" }}>
                  {paragraph}
                </p>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default ChapterTranscript;
