import { useState } from "react";
import Chat from "../components/Chat";
import Flashcards from "../components/Flashcards";
import Summary from "../components/Summary";

function Home() {
  const [activeTab, setActiveTab] = useState("chat");

  return (
    <div style={styles.appContainer}>
      <header style={styles.header}>
        <div style={styles.logo}>YouLearn</div>
        <button style={styles.button}>Upload Video</button>
        <button style={styles.button}>Share</button>
        <button style={{ ...styles.button, ...styles.upgrade }}>Upgrade</button>
      </header>

      <div style={styles.tabs}>
        <button style={activeTab === "chat" ? styles.activeTab : styles.tab} onClick={() => setActiveTab("chat")}>Chat</button>
        <button style={activeTab === "flashcards" ? styles.activeTab : styles.tab} onClick={() => setActiveTab("flashcards")}>Flashcards</button>
        <button style={activeTab === "summary" ? styles.activeTab : styles.tab} onClick={() => setActiveTab("summary")}>Summary</button>
      </div>

      <div style={styles.content}>
        {activeTab === "chat" && <Chat />}
        {activeTab === "flashcards" && <Flashcards />}
        {activeTab === "summary" && <Summary />}
      </div>
    </div>
  );
}

const styles = {
  appContainer: {
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    backgroundColor: "#1a1a1a",
    color: "#fff",
  },
  header: {
    display: "flex",
    alignItems: "center",
    padding: "0.5rem 1rem",
    backgroundColor: "#141414",
    borderBottom: "1px solid #333",
  },
  logo: {
    fontSize: "1.25rem",
    fontWeight: "bold",
    marginRight: "auto",
  },
  button: {
    marginLeft: "0.5rem",
    padding: "0.25rem 0.75rem",
    borderRadius: "4px",
    border: "none",
    background: "transparent",
    color: "#fff",
    cursor: "pointer",
  },
  upgrade: {
    backgroundColor: "#00ff9d",
    color: "#1a1a1a",
  },
  tabs: {
    display: "flex",
    padding: "0.5rem",
    backgroundColor: "#141414",
    borderBottom: "1px solid #333",
  },
  tab: {
    background: "transparent",
    border: "none",
    color: "#a0a0a0",
    padding: "0.5rem 1rem",
    cursor: "pointer",
  },
  activeTab: {
    color: "#fff",
    borderBottom: "2px solid #00ff9d",
  },
  content: {
    flex: 1,
    padding: "1rem",
  },
};

export default Home;
