import { useState } from "react";

function ChapterTranscript() {
  const [activeSection, setActiveSection] = useState("chapter"); // Default to Chapter

  // Styles for the tab-based header
  const styles = {
    header: {
      display: "flex",
      justifyContent: "center",
      backgroundColor: "#1a1a1a",
      padding: "0.75rem 0",
      width: "100%",
      boxShadow: "0px 2px 4px rgba(0, 255, 157, 0.2)",
      borderRadius: "8px",
    },
    tab: {
      padding: "0.75rem 2rem",
      backgroundColor: "transparent",
      color: "#a0a0a0",
      fontSize: "1rem",
      border: "none",
      cursor: "pointer",
      transition: "all 0.3s ease-in-out",
      fontWeight: "bold",
      borderRadius: "6px",
    },
    tabHover: {
      color: "#fff",
      transform: "scale(1.05)",
    },
    activeTab: {
      padding: "0.75rem 2rem",
      backgroundColor: "#00ff9d",
      color: "#000",
      fontSize: "1rem",
      fontWeight: "bold",
      border: "none",
      cursor: "pointer",
      borderRadius: "6px",
      boxShadow: "0px 4px 8px rgba(0, 255, 157, 0.4)",
      transform: "scale(1.1)",
      transition: "all 0.3s ease-in-out",
    },
    contentBox: {
      marginTop: "1rem",
      backgroundColor: "transparent",
      padding: "1rem",
      borderRadius: "6px",
      color: "#fff",
      height: "150%",
      width: "100%",
      maxWidth: "800px",
      textAlign: "center",
      boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
      opacity: 1,
      transform: "translateY(0px)",
      transition: "opacity 0.3s ease, transform 0.3s ease",
    },
  };

  return (
    <div>
      {/* Header Tabs */}
      <div style={styles.header}>
        <button
          style={{
            ...styles.tab,
            ...(activeSection === "chapter" ? styles.activeTab : {}),
          }}
          onMouseEnter={(e) => (e.target.style.color = "#fff")}
          onMouseLeave={(e) =>
            (e.target.style.color = activeSection === "chapter" ? "#000" : "#a0a0a0")
          }
          onClick={() => setActiveSection("chapter")}
        >
          Chapter
        </button>
        <button
          style={{
            ...styles.tab,
            ...(activeSection === "transcript" ? styles.activeTab : {}),
          }}
          onMouseEnter={(e) => (e.target.style.color = "#fff")}
          onMouseLeave={(e) =>
            (e.target.style.color = activeSection === "transcript" ? "#000" : "#a0a0a0")
          }
          onClick={() => setActiveSection("transcript")}
        >
          Transcript
        </button>
      </div>

      {/* Content Section */}
      <div style={styles.contentBox}>
        {activeSection === "chapter" && (
          <div>
            <h3 style={{ marginBottom: "0.5rem", color: "#00ff9d" }}>
              Chapter 1: Introduction
            </h3>
            <p>In this chapter, we introduce the main concepts of the video.</p>
          </div>
        )}
        {activeSection === "transcript" && (
          <div>
            <h3 style={{ marginBottom: "0.5rem", color: "#00ff9d" }}>
              Transcript
            </h3>
            <p>This is the full transcript of the video...</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default ChapterTranscript;
