import { useState } from "react";

const DUMMY_DATA = [
  { id: 1, question: "What is AI?", answer: "AI is artificial intelligence." },
  { id: 2, question: "What is Machine Learning?", answer: "Machine Learning is a subset of AI." },
];

function Flashcards() {
  const [currentCard, setCurrentCard] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);

  return (
    <div
      style={{
        textAlign: "center",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "60vh",
      }}
    >
      <div
        style={{
          padding: "1.5rem",
          backgroundColor: "#222",
          color: "#fff",
          borderRadius: "8px",
          cursor: "pointer",
          width: "100vw",
          height: "100%",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          fontSize: "1.2rem",
          textAlign: "center",
          transition: "transform 0.3s ease-in-out",
          transform: showAnswer ? "rotateY(180deg)" : "rotateY(0deg)",
        }}
        onClick={() => setShowAnswer(!showAnswer)}
      >
        {showAnswer ? DUMMY_DATA[currentCard].answer : DUMMY_DATA[currentCard].question}
      </div>

      <div
        style={{
          marginTop: "1rem",
          display: "flex",
          gap: "10px",
        }}
      >
        <button
          onClick={() => {
            setShowAnswer(false);
            setCurrentCard((currentCard - 1 + DUMMY_DATA.length) % DUMMY_DATA.length);
          }}
          style={{
            padding: "10px 15px",
            fontSize: "1rem",
            cursor: "pointer",
            backgroundColor: "#00ff9d",
            border: "none",
            borderRadius: "5px",
            color: "#1a1a1a",
          }}
        >
          ←
        </button>

        <button
          onClick={() => {
            setShowAnswer(false);
            setCurrentCard((currentCard + 1) % DUMMY_DATA.length);
          }}
          style={{
            padding: "10px 15px",
            fontSize: "1rem",
            cursor: "pointer",
            backgroundColor: "#00ff9d",
            border: "none",
            borderRadius: "5px",
            color: "#1a1a1a",
          }}
        >
          →
        </button>
      </div>
    </div>
  );
}

export default Flashcards;
