import { useState, useEffect, useRef } from "react";
import YouTube from "react-youtube";

function VideoPlayer() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [videoId, setVideoId] = useState(""); // Track YouTube video ID
  const [isUploading, setIsUploading] = useState(false); // State to track whether the user is uploading
  const videoContainerRef = useRef(null); // Ref to the video container for dynamic height

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const handleVideoUpload = () => {
    setIsUploading(true); // Show the input field to upload the video URL
  };

  const handleVideoLinkSubmit = (e) => {
    e.preventDefault();
    const link = e.target.elements.videoLink.value.trim();
    if (link) {
      const videoId = extractYouTubeId(link); // Extract YouTube video ID
      setVideoId(videoId); // Set the video ID
      setIsUploading(false); // Hide the input field
    }
  };

  // Helper function to extract YouTube video ID from URL
  const extractYouTubeId = (url) => {
    const regex = /(?:youtube\.com\/(?:[^\/\n\s]+\/\S+\/|(?:v|e(?:mbed)?)\/|(?:v=|\S*?[?&]v=))|youtu\.be\/)([^"&?\/\s]{11})/;
    const match = url.match(regex);
    return match ? match[1] : "";
  };

  // Dynamically adjust the video player size to fit the full screen
  const getFullScreenDimensions = () => {
    return {
      width: "100vw", // Full width of the viewport
      height: "100vh", // Full height of the viewport
    };
  };

  return (
    <div
      ref={videoContainerRef}
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "100vh", // Make the container take up the full viewport height
        width: "100vw", // Make the container take up the full viewport width
      }}
    >
      {!videoId && !isUploading ? (
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <button
            onClick={handleVideoUpload}
            style={{
              padding: "0.6rem 1.2rem",
              backgroundColor: "#00ff9d",
              color: "#000",
              fontSize: "1rem",
              fontWeight: "bold",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
            }}
          >
            Upload Video
          </button>
        </div>
      ) : isUploading ? (
        <form
          onSubmit={handleVideoLinkSubmit}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <input
            type="text"
            name="videoLink"
            placeholder="Enter YouTube video URL"
            style={{
              padding: "0.6rem 1.2rem",
              marginBottom: "0.5rem",
              borderRadius: "4px",
              border: "1px solid #333",
              width: "80%",
              color: "#fff",
              backgroundColor: "#222",
            }}
            autoFocus
          />
          <button
            type="submit"
            style={{
              padding: "0.6rem 1.2rem",
              backgroundColor: "#00ff9d",
              color: "#000",
              fontSize: "1rem",
              fontWeight: "bold",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
            }}
          >
            Submit
          </button>
        </form>
      ) : (
        <YouTube
          videoId={videoId}
          opts={{
            width: "100%", // Full width of the container
            height: "100%", // Full height of the container
            playerVars: { autoplay: 1 },
          }}
          onPlay={() => setIsPlaying(true)}
          onPause={() => setIsPlaying(false)}
        />
      )}
    </div>
  );
}

export default VideoPlayer;
