import { useState } from "react";

function ChapterTranscript() {
  const [activeSection, setActiveSection] = useState(null);

  // JSON data: Chapters are timestamps of a YouTube video
  const data = {
    chapters: [
      {
        id: 1,
        timestamp: "00:30",
        title: "Introduction to AI",
        content: "This section covers the basics of AI.",
        ytLink: "https://www.youtube.com/watch?v=EXAMPLE&t=30s",
      },
      {
        id: 2,
        timestamp: "02:15",
        title: "What is Machine Learning?",
        content: "An introduction to Machine Learning and its applications.",
        ytLink: "https://www.youtube.com/watch?v=EXAMPLE&t=135s",
      },
      {
        id: 3,
        timestamp: "05:40",
        title: "Deep Learning Overview",
        content: "Understanding the fundamentals of Deep Learning.",
        ytLink: "https://www.youtube.com/watch?v=EXAMPLE&t=340s",
      },
    ],
    transcript: {
      title: "Full Transcript",
      content:
        "This is the full transcript of the video, including explanations, examples, and key takeaways...",
    },
  };

  return (
    <div
      style={{
        marginTop: "1rem",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      {/* Buttons */}
      <div
        style={{
          marginTop: "1rem",
          display: "flex",
          justifyContent: "center",
          gap: "1rem",
        }}
      >
        <button
          onClick={() => setActiveSection("chapter")}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: activeSection === "chapter" ? "#00ff9d" : "#333",
            color: "#fff",
            fontSize: "1rem",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            transition: "background-color 0.3s ease",
          }}
        >
          Show Chapters
        </button>

        <button
          onClick={() => setActiveSection("transcript")}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: activeSection === "transcript" ? "#00ff9d" : "#333",
            color: "#fff",
            fontSize: "1rem",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            transition: "background-color 0.3s ease",
          }}
        >
          Show Transcript
        </button>
      </div>

      {/* Chapters Section */}
      {activeSection === "chapter" && (
        <div
          style={{
            marginTop: "1rem",
            backgroundColor: "#2c2c2c",
            padding: "1rem",
            borderRadius: "6px",
            color: "#fff",
            width: "100%",
            maxWidth: "800px",
            textAlign: "center",
            boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
            transition: "opacity 0.3s ease, transform 0.3s ease",
          }}
        >
          <h3 style={{ color: "#00ff9d" }}>YouTube Chapters</h3>
          {data.chapters.map((chapter) => (
            <div key={chapter.id} style={{ marginBottom: "1rem" }}>
              <p>
                ‚è± <strong>{chapter.timestamp}</strong> -{" "}
                <a
                  href={chapter.ytLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{ color: "#00ff9d", textDecoration: "none" }}
                >
                  {chapter.title}
                </a>
              </p>
              <p>{chapter.content}</p>
            </div>
          ))}
        </div>
      )}

      {/* Transcript Section */}
      {activeSection === "transcript" && (
        <div
          style={{
            marginTop: "1rem",
            backgroundColor: "#2c2c2c",
            padding: "1rem",
            borderRadius: "6px",
            color: "#fff",
            width: "100%",
            maxWidth: "800px",
            textAlign: "center",
            boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
            transition: "opacity 0.3s ease, transform 0.3s ease",
          }}
        >
          <h3 style={{ color: "#00ff9d" }}>{data.transcript.title}</h3>
          <p>{data.transcript.content}</p>
        </div>
      )}
    </div>
  );
}

export default ChapterTranscript;
